export default function formatMoney(amount = 0) {
    const options  = {
        style: 'currency',
        currency: 'USD',
        minimumFractionDigits: 2,

    }
    const formatter = Intl.NumberFormat('en-Us', options);
    return formatter.format(amount)
}
