//this is where you can add data to the database

import useForm from "../lib/useForm";

export default function CreateProduct() {
  //here we are destructuring the values that are 
  //returned from the useForm custom hook we built
  const {inputs, handleChange} = useForm({
      name: 'dummyName',
      price: 23343,
      description: 'dummyDescription'
  });
  return (
    <form>
      <label htmlFor="name">
        <input
          type="text"
          id="name"
          name="name"
          placeholder="name"
          value={inputs.name}
          onChange={handleChange}
        />
      </label>
      <label htmlFor="price">
        <input
          type="number"
          id="price"
          name="price"
          placeholder="price"
          value={inputs.price}
          onChange={handleChange}
        />
      </label>
      <button onClick={clearForm}>Clear Form</button>
    </form>
  );
}
