//this is where you can add data to the database

export default function CreateProduct() {
  //here we are destructuring the values that are 
  //returned from the useForm custom hook we built
  const {inputs, handleChange} = useForm({
      name: 'dummyName',
      price: 23343,
      description: 'dummyDescription'
  });
  return (
    <form>
      <label htmlFor="name">
        <input
          type="text"
          id="name"
          name="name"
          placeholder="name"
          value={inputs.name}
          onChange={handleChange}
        />
      </label>
      <label htmlFor="price">
        <input
          type="text"
          id="price"
          name="price"
          placeholder="price"
          value={inputs.price}
          onChange={(e) => {
            setName(e.target.value);
          }}
        />
      </label>
    </form>
  );
}
