//this is where you can add data to the database
import Form from './styles/Form'
import useForm from "../lib/useForm";

export default function CreateProduct() {
  //here we are destructuring the values that are 
  //returned from the useForm custom hook we built
  const {inputs, handleChange, resetForm, clearForm} = useForm({
      name: 'dummyName',
      price: 23343,
      description: 'dummyDescription'
  });
  return (
    <Form>
        <fieldSet>
        <label htmlFor="image">
          image
        <input
          type="file"
          id="image"
          name="image"
          placeholder="name"
          value={inputs.name}
          onChange={handleChange}
        />
      </label>
      <label htmlFor="name">
          Name
        <input
          type="text"
          id="name"
          name="name"
          placeholder="name"
          value={inputs.name}
          onChange={handleChange}
        />
      </label>
      <label htmlFor="price">
Price
      <input
          type="number"
          id="price"
          name="price"
          placeholder="price"
          value={inputs.price}
          onChange={handleChange}
        />
      </label>
<button type="submit">+ Add Product</button>
</fieldSet>
    </Form>
  );
}
