  }
//clear all of the 
  function clearForm() {
      const blankState = object.fromEntries(object.entries(inputs).map(([key, value]) => [key, '']))
      setInputs(blankState)
  }
  //return the data we want to surface from this custom hook
  return {
    inputs,
    handleChange,
    resetForm,
    clearForm
  };
}
